import { Component } from '@angular/core';
import { CourseService } from './course.service';
import { Router } from '@angular/router';
import { MatDialog } from '@angular/material/dialog';

interface Courset {
  title: string;
  description: string;
  imageurl: string;
}

@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.css']
})
export class CoursesComponent {
  courses: Courset[] = [];
  selectedCourse: Courset | undefined;

  constructor(private service: CourseService, private router: Router, private dialog: MatDialog) {}

  ngOnInit(): void {
    this.getCourses();
  }

  getCourses(): void {
    this.service.getCourses().subscribe((courses: Courset[]) => {
      this.courses = courses;
    });
  }

  openModal(course: Courset): void {
    this.selectedCourse = course;
    // Open the modal dialog
    const dialogRef = this.dialog.open(CourseModalComponent, {
      width: '400px',
      data: this.selectedCourse
    });

    // Subscribe to the afterClosed event
    dialogRef.afterClosed().subscribe(() => {
      // Handle any actions after the modal is closed, if needed
    });
  }
}
