import { Component, OnInit } from '@angular/core';
import { Course1 } from '../model/course1';
import { Courset} from '../model/Courset';
import { CourseService } from '../course.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-updatecourse',
  templateUrl: './updatecourse.component.html',
  styleUrls: ['./updatecourse.component.css']
})
export class UpdatecourseComponent implements OnInit {
  course: Course1 = new Course1(this.title,this.description, this.topics, this.imageFile);
  courset: Courset[] = [];
  courseId: number = 0;
  courset: Courset = new Courset();

  constructor(private courseService: CourseService,private route: ActivatedRoute,
    private router: Router) { }


    ngOnInit(): void {
      this.courseId = this.route.snapshot.params['id'];
      this.courseService.getCourseById(this.courseId).subscribe(
        (courset) => {
          this.courset=courset;          
        },
        (error) => {
          console.log('Error retrieving product:', error);
        }
      );
    }
    onImageFileChange(event: any) {
      const file = event.target.files[0];
      this.course.imageFile = file;
    }

    saveUpdatedProduct(): void {
       this.course.title = this.courset.title;
            this.course.description = this.courset.description;
  this.courseService.updateProduct(this.course,this.courseId).subscribe(
        (response) => {
          console.log('Product updated successfully');
          this.getCourses();
        },
        (error) => {
          console.log('Error updating product:', error);
        }
      );
    }
    }
