import { Component } from '@angular/core';
import { CourseService } from '../course.service';
import { Router } from '@angular/router';
import { Courset } from '../model/Courset';

@Component({
  selector: 'app-courses',
  templateUrl: './courses.component.html',
  styleUrls: ['./courses.component.css']
})
export class CoursesComponent {

  courses: Courset[] = [];
  selectedcourse: Courset | undefined;


  constructor(private service:CourseService,private router: Router){}

  ngOnInit(): void {
    this.getCourses();
  }

  getCourses(): void {
    this.service.getCourses().subscribe((courses) => {
      this.courses = courses;
    });
  }
  openModal(course: Course): void {
    this.selectedCourse = course;
    $('#modalTitle').text(course.title);
    $('#modalDescription').text(course.description);
    $('#modalEnrollButton').click(() => {
      // Handle enroll button click
      console.log('Enroll button clicked for course:', course.title);
      // Add your enroll logic here
    });
    $('#courseModal').modal('show'); // Use 'modal' method to show the modal form
  }

  closeModal(): void {
    $('#courseModal').modal('hide'); // Use 'modal' method to hide the modal form
  }
}
}
