import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Topic } from '../Topic';
import { CourseService } from '../course.service';
import { Course1 } from '../model/course1';

@Component({
  selector: 'app-admincourse',
  templateUrl: './admincourse.component.html',
  styleUrls: ['./admincourse.component.css']
})
export class AdmincourseComponent {
  title: string = '';
  description: string = '';
  topics: Topic[] = [];

  constructor(private http: HttpClient, private courseservice: CourseService) {}


  addTopic(): void {
    const topic = new Topic('',undefined);
    this.topics.push(topic);
  }

  deleteTopic(index: number): void {
    this.topics.splice(index, 1);
  }

  handleFileInput(event: any, index?: number): void {
    const fileList: FileList = event.target.files;
    if (fileList.length > 0) {
      if (index !== undefined) {
        this.topics[index].content = fileList[0];
      } else {
        // Handle the file input for course description
        this.description = fileList[0].name;
      }
    }
  }




  addCourse(): void {
    const course = new Course1(this.title, this.description, this.topics);
    console.log(course);
    this.courseservice.addCourse(course).subscribe(
      (response) => {
        console.log('Course added successfully:', response);
        // Do something with the response if needed
      },
      (error) => {
        console.error('Failed to add course:', error);
        // Handle the error appropriately
      }
    );
  }

}
