import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { CourseService } from '../course.service';

import { Topic } from '../Topic';
import { Course1 } from '../model/course1';

@Component({
  selector: 'app-addcourse',
  templateUrl: './addcourse.component.html',
  styleUrls: ['./addcourse.component.css']
})
export class AddcourseComponent {
  course: Course1 = new Course1();
  title: string = '';
  description: string = '';
  topics: Topic[] = [];
  image!: File | null;

  constructor(private http: HttpClient, private courseservice: CourseService) {}


  addTopic(): void {
    const topic = new Topic('',undefined);
    this.topics.push(topic);
  }

  deleteTopic(index: number): void {
    this.topics.splice(index, 1);
  }

  handleFileInput(event: any, index?: number): void {
    const fileList: FileList = event.target.files;
    if (fileList.length > 0) {
      if (index !== undefined) {
        this.topics[index].content = fileList[0];
      } else {
        // Handle the file input for course description
        this.description = fileList[0].name;
      }
    }
  }
  onImageFileChange(event: any) {
    const file = event.target.files[0];
    this.course.imageFile = file;
  }



  addCourse(): void {


    this.courseservice.addCourse(this.course).subscribe(
      (response) => {
        console.log('Course added successfully:', response);
        alert('Course added successfully');
        // Do something with the response if needed
      },
      (error) => {
        console.error('Failed to add course:', error);
        // Handle the error appropriately
      }
    );
  }

}



  <app-navbar2></app-navbar2>
  <div class="container">
    <h1>Add Course</h1>
    <form (ngSubmit)="addCourse()" #courseForm="ngForm">

        <div class="form-group">
          <label for="title">Title</label>
          <input type="text" class="form-control" id="title" name="title" [(ngModel)]="title" required>
        </div>
        <div class="form-group">
          <label for="description">Description</label>
          <input type="text" class="form-control" id="description" name="description" [(ngModel)]="description" required>
        </div>
        <div class="form-group">
          <label for="Course Image">Course-Image</label>
          <input type="file" class="form-control" id="imageFile" (change)="onImageFileChange($event)" name="imageFile">
        </div>
        <div class="form-group">
          <label for="topics">Topics</label>
          <table class="table">
            <thead>
              <tr>
                <th>Topic Title</th>
                <th>Topic File</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let topic of topics; let i = index">
                <td>
                  <input type="text" class="form-control" name="topicTitle{{i}}" [(ngModel)]="topics[i].name" required>
                </td>
                <td>
                  <input type="file" class="form-control-file" name="topicFile{{i}}" (change)="handleFileInput($event, i)" required>


                </td>
                <td>
                  <button (click)="deleteTopic(i)" type="button" class="btn btn-danger">Delete</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
        <button (click)="addTopic()" type="button" class="btn btn-primary">Add Topic</button>
        <button type="submit" class="btn btn-primary" [disabled]="!courseForm.form.valid">Add Course</button> 
